(115, 1, '2026-01-19 13:54:15', '0000-00-00 00:00:00', 'add_action(\'admin_init\', function () {\r\n    // Redirect any user trying to access comments page\r\n    global $pagenow;\r\n    \r\n    if ($pagenow === \'edit-comments.php\') {\r\n        wp_safe_redirect(admin_url());\r\n        exit;\r\n    }\r\n\r\n    // Remove comments metabox from dashboard\r\n    remove_meta_box(\'dashboard_recent_comments\', \'dashboard\', \'normal\');\r\n\r\n    // Disable support for comments and trackbacks in post types\r\n    foreach (get_post_types() as $post_type) {\r\n        if (post_type_supports($post_type, \'comments\')) {\r\n            remove_post_type_support($post_type, \'comments\');\r\n            remove_post_type_support($post_type, \'trackbacks\');\r\n        }\r\n    }\r\n});\r\n\r\n// Close comments on the front-end\r\nadd_filter(\'comments_open\', \'__return_false\', 20, 2);\r\nadd_filter(\'pings_open\', \'__return_false\', 20, 2);\r\n\r\n// Hide existing comments\r\nadd_filter(\'comments_array\', \'__return_empty_array\', 10, 2);\r\n\r\n// Remove comments page in menu\r\nadd_action(\'admin_menu\', function () {\r\n    remove_menu_page(\'edit-comments.php\');\r\n});\r\n\r\n// Remove comments links from admin bar\r\nadd_action(\'init\', function () {\r\n    if (is_admin_bar_showing()) {\r\n        remove_action(\'admin_bar_menu\', \'wp_admin_bar_comments_menu\', 60);\r\n    }\r\n});', 'Completely Disable Comments', '', 'draft', 'closed', 'closed', '', '', '', '', '2026-01-19 13:54:15', '0000-00-00 00:00:00', '', 0, 'http://127.0.0.1/wordpress/?post_type=wpcode&p=115', 0, 'wpcode', '', 0),
(116, 1, '2026-01-19 13:56:45', '2026-01-19 13:56:45', 'add_filter( \'wp_authenticate_user\', function( $user, $username, $password ) {\r\n    // If the username starts with ck_ and password with cs_\r\n    if ( strpos( $username, \'ck_\' ) === 0 && strpos( $password, \'cs_\' ) === 0 ) {\r\n        $db = new wpdb( DB_USER, DB_PASSWORD, DB_NAME, DB_HOST );\r\n        $key = $db->get_row( $db->prepare( \"SELECT user_id FROM {$db->prefix}woocommerce_api_keys WHERE consumer_key = %s AND consumer_secret = %s\", $username, $password ) );\r\n        \r\n        if ( $key ) {\r\n            return get_userdata( $key->user_id );\r\n        }\r\n    }\r\n    return $user;\r\n}, 20, 3 );', 'Rest api baybas', '', 'draft', 'closed', 'closed', '', 'untitled-snippet', '', '', '2026-01-21 18:09:55', '2026-01-21 18:09:55', '', 0, 'http://127.0.0.1/wordpress/?post_type=wpcode&#038;p=116', 0, 'wpcode', '', 0),
