(38, 'woocommerce-WCReturnsWarranty-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce Returns and Warranty Requests extension', '<strong>Your store requires a security update for the Returns and Warranty Requests extension</strong>.  Please update to the latest version of the WooCommerce Returns and Warranty Requests extension immediately to address a potential vulnerability discovered on May 31.', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(39, 'woocommerce-WCOPC-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce One Page Checkout', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce One Page Checkout extension</strong>. The fix for this vulnerability was released for this extension on June 13th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(40, 'woocommerce-WCGC-July-2023-update-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce GoCardless Extension', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce GoCardless extension</strong>. The fix for this vulnerability was released on July 4th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(41, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(42, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(43, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(44, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(45, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(46, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(47, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'sirt-woo-2024-11-xss-admin', 'update', 'en_US', 'An important update is required for WooCommerce', 'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'paypal-braintree-sdk-depreciation-2025-02-20', 'update', 'en_US', 'Update required for your PayPal Braintree extension', 'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'uk-vat-notice-2025-04-30', 'warning', 'en_US', 'UK VAT numbers are no longer supported', 'Due to recent HM Revenue &amp; Customs (HMRC) VAT API changes, your EU VAT Number for WooCommerce extension can no longer validate UK VAT numbers. HMRC now requires merchants to register an API key, which this extension does not currently support. We recommend EU/UK VAT Validation Manager for WooCommerce as a replacement', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'paypal-braintree-sdk-depreciation-2025-05-30', 'update', 'en_US', 'Action required: Update the PayPal Braintree extension', 'Your PayPal Braintree extension is using an outdated SDK and will stop processing payments after June 30, 2025, due to a root SSL certificate expiration. To avoid service disruption, update to version 3.2.4 or newer immediately.', '{}', 'pending', 'woocommerce.com', '2026-01-19 10:50:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'paypal-carding-recaptcha-dec-25', 'update', 'en_US', 'Activate PayPal fraud management', 'PayPal detected increased suspicious card activity in market. Please enable fraud protection in your PayPal Payment settings by enabling CAPTCHA for PayPal Payments.', '{}', 'unactioned', 'woocommerce.com', '2026-01-19 10:51:49', NULL, 0, 'plain', '', 1, 0, 'info'),
(56, 'paypal-carding-recaptcha-jan-26', 'update', 'en_US', 'Fraud protection is now required — enable today', 'Card networks like Visa and Mastercard now require fraud prevention controls, and non-compliance may result in fines and processing restrictions. Please enable reCAPTCHA in your PayPal Payments settings to help protect your store and maintain compliance.', '{}', 'unactioned', 'woocommerce.com', '2026-01-19 10:51:49', NULL, 0, 'plain', '', 1, 0, 'info'),
(57, 'ppcp-recaptcha-protection-note12', 'info', 'en_US', 'Fraud protection is now required — enable today', 'Card networks like Visa, Mastercard and American Express now require fraud prevention controls, and non-compliance may result in fines and processing restrictions. Please enable reCAPTCHA in your PayPal Payments settings to help protect your store and maintain compliance.', '{}', 'unactioned', 'woocommerce-paypal-payments/woocommerce-paypal-payments.php', '2026-01-19 10:51:51', NULL, 0, 'plain', '', 0, 1, 'info'),
(58, 'cocart-wc-admin-help-improve', 'info', 'en_US', 'Help improve CoCart', 'We love your input to shape the future of CoCart together. Feel free to share any feedback, ideas or suggestions that you have.', '{}', 'unactioned', 'cocart', '2026-01-19 21:14:40', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'cocart-wc-admin-need-help', 'info', 'en_US', 'Need help with CoCart?', 'You can ask for help by joining the CoCart community on Discord.', '{}', 'unactioned', 'cocart', '2026-01-19 21:14:40', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'cocart-wc-admin-thanks-install', 'info', 'en_US', 'Thank you for installing CoCart!', 'Now you are ready to start developing your headless store. Visit the documentation site to learn how to access the API, view examples and find many action hooks and filters and more.', '{}', 'unactioned', 'cocart', '2026-01-19 21:14:40', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'cocart-wc-admin-upgrade', 'info', 'en_US', 'Ready to take your headless store to the next level?', 'Upgrade CoCart and unlock more cart features and supported WooCommerce extensions.', '{}', 'unactioned', 'cocart', '2026-01-19 21:14:40', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2026-01-21 17:25:15', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2026-01-21 17:25:15', NULL, 0, 'plain', '', 0, 0, 'info'),
(64, 'wc-admin-orders-milestone', 'info', 'en_US', 'First order received', 'Congratulations on getting your first order! Now is a great time to learn how to manage your orders.', '{\"current_milestone\":1}', 'unactioned', 'woocommerce-admin', '2026-01-22 15:52:16', NULL, 0, 'plain', '', 0, 0, 'info'),
(65, 'wc-admin-marketing-jetpack-backup', 'marketing', 'en_US', 'Protect your WooCommerce Store with Jetpack Backup.', 'Store downtime means lost sales. One-click restores get you back online quickly if something goes wrong.', '{}', 'unactioned', 'woocommerce-admin-notes', '2026-01-22 20:17:49', NULL, 0, 'thumbnail', 'http://127.0.0.1/wordpress/wp-content/plugins/woocommerce/assets/images/admin_notes/marketing-jetpack-2x.png', 0, 0, 'info'),
(66, 'existing_square_bitcoin_promotion_q1_2026', 'marketing', 'en_US', 'Take bitcoin payments in Square with 0% fees in 2026', 'Your Square POS can now accept in-person bitcoin payments, with near-instant settlement and no chargebacks. Enable it today and pay zero fees through 2026. T&amp;Cs apply.', '{}', 'pending', 'woocommerce.com', '2026-01-22 20:17:51', NULL, 0, 'plain', '', 0, 0, 'info'),
(67, 'prospects_square_bitcoin_promotion_q1_2026', 'marketing', 'en_US', 'Take bitcoin payments in Square with 0% fees in 2026', 'Accept in-person <a href=\"https://woocommerce.com/posts/square-bitcoin-launch\">bitcoin payments</a> with Square POS — and pay zero fees on all bitcoin transactions through 2026. Get near-instant settlement and total protection from chargebacks.', '{}', 'pending', 'woocommerce.com', '2026-01-22 20:17:51', NULL, 0, 'plain', '', 0, 0, 'info');
